import React, { useState } from 'react';
import { SendHorizontal } from 'lucide-react';

const App = () => {
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleSend = async () => {
    if (input.trim()) {
      setMessages([...messages, { text: input, sender: 'user' }]);
      setInput('');
      setLoading(true);

      setTimeout(() => {
        setMessages(prev => [...prev, { text: 'Generated portfolio based on your input.', sender: 'ai' }]);
        setLoading(false);
      }, 2000); // Simulating a delay
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      handleSend();
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-950 to-purple-800 flex flex-col items-center justify-center text-white p-6">
      <div className="h-96 w-full max-w-4xl overflow-y-auto p-4">
        {messages.map((msg, index) => (
          <div key={index} className={`mb-4 text-lg ${msg.sender === 'user' ? 'text-right' : 'text-left'}`}> 
            <span className={msg.sender === 'user' ? 'bg-purple-700 p-3 rounded-xl inline-block' : 'bg-purple-800 p-3 rounded-xl inline-block'}>
              {msg.text}
            </span>
          </div>
        ))}
        {loading && (
          <div className="flex justify-center mt-4">
            <div className="w-8 h-8 border-4 border-white border-t-transparent rounded-full animate-spin"></div>
          </div>
        )}
      </div>

      <div className="flex items-center w-full max-w-4xl p-4">
        <input 
          className="flex-1 p-3 border rounded-xl outline-none bg-purple-950 text-white placeholder-gray-400" 
          type="text" 
          placeholder="Describe your portfolio..." 
          value={input} 
          onChange={(e) => setInput(e.target.value)} 
          onKeyDown={handleKeyDown}
        />
        <button onClick={handleSend} className="ml-3 bg-purple-700 text-white p-3 rounded-xl">
          <SendHorizontal size={20} />
        </button>
      </div>
    </div>
  );
};

export default App;
