import React, { useState } from 'react';
import { SendHorizontal } from 'lucide-react';
import axios from 'axios';

const App = () => {
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState([]);

  const handleSend = async () => {
    if (input.trim()) {
      setMessages([...messages, { text: input, sender: 'user' }]);
      setInput('');
      setMessages(prev => [...prev, { text: 'G...', sender: 'ai' }]);

      try {
        const response = await axios.post(
          'https://generativelanguage.googleapis.com/v1beta/models/gemini-1.5-pro:generateContent',
          {
            contents: [{ parts: [{ text: input }] }]
          },
          {
            headers: { 'Content-Type': 'application/json' },
            params: { key: 'AIzaSyDIcfjdJCcFXJhBry1aX8OgW3zP_Pr4cZg' }
          }
        );

        const aiResponse = response?.data?.candidates?.[0]?.content?.parts?.[0]?.text || 'No response';
        setMessages(prev => [...prev, { text: aiResponse, sender: 'ai' }]);
      } catch (error) {
        console.error('Error:', error);
        setMessages(prev => [...prev, { text: 'Failed to generate portfolio. Please try again.', sender: 'ai' }]);
      }
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-950 to-purple-800 flex items-center justify-center p-6">
      <div className="w-full max-w-3xl bg-purple-900 rounded-2xl shadow-xl overflow-hidden">
        <div className="h-96 overflow-y-auto p-4">
          {messages.map((msg, index) => (
            <div key={index} className={`mb-2 text-sm ${msg.sender === 'user' ? 'text-right' : 'text-left'}`}> 
              <span className={`inline-block p-3 rounded-lg ${msg.sender === 'user' ? 'bg-purple-700 text-white' : 'bg-purple-800 text-white'}`}>{msg.text}</span>
            </div>
          ))}
        </div>
        <div className="flex items-center p-4 border-t border-purple-800">
          <input 
            className="flex-1 p-3 border rounded-xl outline-none bg-purple-950 text-white placeholder-gray-400" 
            type="text" 
            placeholder="Describe your portfolio..." 
            value={input} 
            onChange={(e) => setInput(e.target.value)} 
          />
          <button onClick={handleSend} className="ml-3 bg-purple-700 text-white p-3 rounded-xl">
            <SendHorizontal size={20} />
          </button>
        </div>
      </div>
    </div>
  );
};

export default App;
